INSERT INTO `tintuc` (`id`, `tieu_de`, `hinh_anh`, `noi_dung`, `created_at`, `updated_at`) VALUES
(19, 'Cách kiểm tra máy tính của bạn có được lên Windows 11 hay không bằng Windows PC Health Check trong một nốt nhạc', 'image/87EwWdg1kR5eLZa7PcCf2jdzZA9NThUt4OIbsemK.jpg', '<h1>C&aacute;ch kiểm tra m&aacute;y t&iacute;nh của bạn c&oacute; được l&ecirc;n Windows 11 hay kh&ocirc;ng bằng Windows PC Health Check trong một nốt nhạc</h1>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tin-tuc/profile/0\">Nguyễn Ngọc Huy</a>&nbsp;3 giờ trước</p>\r\n\r\n<p><img alt=\"Cách xem máy tính của bạn có lên Windows 11 được không\" src=\"https://cdn.tgdd.vn/Files/2021/06/25/1363188/cach-xem-may-tinh-cua-ban-co-len-windows-11-duoc-khong_1280x720-800-resize.jpg\" /></p>\r\n\r\n<h2>Microsoft đ&atilde; ch&iacute;nh thức giới thiệu&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/cach-kiem-tra-may-tinh-cua-ban-co-duoc-len-windows-11-hay-khong-1363188\" target=\"_blank\">Windows 11</a>&nbsp;tại sự kiện Microsoft Event 2021 vừa qua.&nbsp;Windows 11 sẽ l&agrave; kh&ocirc;ng gian s&aacute;ng tạo cho bạn với nhiều trải nghiệm mới mẻ, từ menu Start cho tới c&aacute;ch thức bạn kết nối với bạn b&egrave;, tin tức, tr&ograve; chơi v&agrave; c&aacute;c nội dung mới. Sau đ&acirc;y l&agrave; c&aacute;ch kiểm tra xem&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\">m&aacute;y t&iacute;nh</a>&nbsp;của bạn c&oacute; đủ điều kiện để n&acirc;ng cấp l&ecirc;n Windows 11 hay kh&ocirc;ng.</h2>\r\n\r\n<h3><strong>Hướng dẫn kiểm tra m&aacute;y t&iacute;nh của bạn c&oacute; được l&ecirc;n Windows 11 kh&ocirc;ng</strong></h3>\r\n\r\n<p><strong>Bước 1</strong>. Bạn nhấn v&agrave;o đường link b&ecirc;n dưới để tải về ứng dụng Windows PC Health Check của Microsoft.</p>\r\n\r\n<ul>\r\n	<li><a href=\"https://aka.ms/GetPCHealthCheckApp\" target=\"_blank\">Tải Windows PC Health Check</a></li>\r\n</ul>\r\n\r\n<p><img alt=\"Cách xem máy tính của bạn có lên Windows 11 được không-1\" src=\"https://cdn.tgdd.vn/Files/2021/06/25/1363188/cach-xem-may-tinh-cua-ban-co-len-windows-11-duoc-khong-1_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p><strong>Bước 2</strong>. Mở file bạn vừa tải về, bạn chọn&nbsp;<strong>I accept the terms in the License Agreement</strong>&nbsp;v&agrave; nhấn&nbsp;<strong>Install</strong>.</p>\r\n\r\n<p><img alt=\"Cách xem máy tính của bạn có lên Windows 11 được không-2\" src=\"https://cdn.tgdd.vn/Files/2021/06/25/1363188/cach-xem-may-tinh-cua-ban-co-len-windows-11-duoc-khong-2_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p><strong>Bước 3</strong>. C&agrave;i đặt th&agrave;nh c&ocirc;ng&nbsp;<strong>PC Health Check</strong>, tại banner&nbsp;<strong>Introducing Windows 11</strong>, bạn chọn&nbsp;<strong>Check now</strong>.</p>\r\n\r\n<p><img alt=\"Cách xem máy tính của bạn có lên Windows 11 được không-3\" src=\"https://cdn.tgdd.vn/Files/2021/06/25/1363188/cach-xem-may-tinh-cua-ban-co-len-windows-11-duoc-khong-3_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p><strong>Bước 4</strong>. Nếu m&aacute;y t&iacute;nh bạn đủ điều kiện n&acirc;ng cấp l&ecirc;n Windows 11, ứng dụng sẽ hiện tick xanh v&agrave; hiện th&ocirc;ng b&aacute;o&nbsp;<strong>This PC can run Windows 11</strong>. C&ograve;n nếu kh&ocirc;ng được, bạn vẫn sẽ nhận được c&aacute;c bản cập nhật Windows 10 đều đặn cho đến ng&agrave;y 14 th&aacute;ng 10 năm 2025.</p>\r\n\r\n<p><img alt=\"Cách xem máy tính của bạn có lên Windows 11 được không-4\" src=\"https://cdn.tgdd.vn/Files/2021/06/25/1363188/cach-xem-may-tinh-cua-ban-co-len-windows-11-duoc-khong-4_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p><strong>Bước 5</strong>. Bạn nhấn&nbsp;<strong>Learn More</strong>&nbsp;để t&igrave;m hiểu th&ecirc;m nhiều th&ocirc;ng tin về Windows 11 mới v&agrave; c&aacute;c c&acirc;u hỏi thường gặp về hệ điều h&agrave;nh mới.</p>\r\n\r\n<p><img alt=\"Cách xem máy tính của bạn có lên Windows 11 được không\" src=\"https://cdn.tgdd.vn/Files/2021/06/25/1363188/cach-xem-may-tinh-cua-ban-co-len-windows-11-duoc-khong-5-_1707x833-800-resize.jpg\" /></p>\r\n\r\n<p>M&aacute;y t&iacute;nh của bạn c&oacute; đủ điều kiện n&acirc;ng cấp l&ecirc;n Windows 11 kh&ocirc;ng? H&atilde;y b&igrave;nh luận b&ecirc;n dưới nh&eacute;. Nhớ ấn Like v&agrave; Chia sẻ ủng hộ m&igrave;nh.</p>', '2021-06-25 09:04:46', '2021-06-26 10:36:46'),
(20, 'Hướng dẫn cách tối ưu hóa chơi game trên những chiếc Laptop Gaming, đã mạnh mẽ nay còn nhanh hơn nữa', 'image/LxpykL29ENgLmWyktRUTRCIknQZoRCsz3YWXWY1f.jpg', '<h1>Hướng dẫn c&aacute;ch tối ưu h&oacute;a chơi game tr&ecirc;n những chiếc Laptop Gaming, đ&atilde; mạnh mẽ nay c&ograve;n nhanh hơn nữa</h1>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tin-tuc/profile/0\">Nguyễn Thang Khang</a>&nbsp;1 ng&agrave;y trước</p>\r\n\r\n<p><img alt=\"tangtoclaptopgaming\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362832/tangtoclaptopgaming8-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<h2>Trong những trận chiến game căng thẳng chỉ cần hơn k&eacute;m nhau v&agrave;i con số FPS đ&atilde; gi&uacute;p cho đội của bạn chiến thắng được những đối thủ rồi. V&igrave; thế h&ocirc;m nay m&igrave;nh sẽ gi&uacute;p c&aacute;c bạn tăng tốc chơi game tr&ecirc;n những chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-gaming\" target=\"_blank\">Laptop Gaming</a>&nbsp;v&ocirc; c&ugrave;ng mạnh mẽ. Thử ngay những&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/huong-dan-cach-toi-uu-hoa-choi-game-tren-nhung-chiec-laptop-gaming-1362832\" target=\"_blank\">mẹo tăng tốc chơi game tr&ecirc;n Laptop Gaming</a>&nbsp;n&agrave;y v&agrave; chiến game ngay th&ocirc;i n&agrave;o!</h2>\r\n\r\n<h3><strong>1. Bật &quot;Windows hardware graphics scheduling&quot;</strong></h3>\r\n\r\n<p>Mục C&agrave;i đặt phần mềm lu&ocirc;n l&agrave; nơi đầu ti&ecirc;n m&agrave; bạn cần để &yacute; đến. Với t&iacute;nh năng mới được Update đến từ Windows 10, &quot;Windows hardware graphics scheduling&quot; sẽ gi&uacute;p bạn cải thiện được từ 5 đến 10 FPS trong game.</p>\r\n\r\n<p><strong>Bước 1:</strong>&nbsp;Anh em v&agrave;o&nbsp;<strong>C&agrave;i đặt hệ thống</strong>&nbsp;v&agrave; chọn&nbsp;<strong>System</strong>.</p>\r\n\r\n<p><img alt=\"tangtoclaptopgaming\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362832/tangtoclaptopgaming5-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<p><strong>Bước 2:</strong>&nbsp;Ở mục Display anh em k&eacute;o xuống phần&nbsp;<strong>Multiple Display&nbsp;</strong>v&agrave; chọn v&agrave;o&nbsp;<strong>Graphics Settings</strong>.</p>\r\n\r\n<p><img alt=\"tangtoclaptopgaming\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362832/tangtoclaptopgaming4-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<p><strong>Bước 3:</strong>&nbsp;Cuối c&ugrave;ng anh em bật t&iacute;nh năng&nbsp;<strong>Windows hardware graphics scheduling l&ecirc;n.</strong></p>\r\n\r\n<p><img alt=\"tangtoclaptopgaming\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362832/tangtoclaptopgaming3-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<h3><strong>2. T&ugrave;y chỉnh điện năng ti&ecirc;u thụ đầu v&agrave;o của laptop</strong></h3>\r\n\r\n<p>Khi chơi game anh em c&oacute; thể dồn hết sức mạnh của chiếc laptop gaming v&agrave;o chiếc m&aacute;y để cho ra FPS cao hơn. Với việc tập trung như vầy laptop anh em c&oacute; thể to&agrave;n t&acirc;m, to&agrave;n &yacute; để khởi chạy game một c&aacute;ch nhanh nhất.</p>\r\n\r\n<p><strong>Bước 1:</strong>&nbsp;Trong mục&nbsp;<strong>C&agrave;i đặt</strong>, anh em bắt đầu t&igrave;m kiếm&nbsp;<strong>Graphics Settings</strong>.</p>\r\n\r\n<p><img alt=\"tangtoclaptopgaming\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362832/tangtoclaptopgaming6-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<p><strong>Bước 2:</strong>&nbsp;Tại đường dẫn&nbsp;<strong>Choose an app to set preference.</strong>&nbsp;Anh em chọn v&agrave;o&nbsp;<strong>tựa game&nbsp;</strong>m&agrave; anh em muốn tăng tốc l&agrave; xong nh&eacute;.</p>\r\n\r\n<p><img alt=\"tangtoclaptopgaming\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362832/tangtoclaptopgaming7-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<h3><strong>3. Bật chế độ Performance cho laptop</strong></h3>\r\n\r\n<p>Đ&acirc;y l&agrave; chế độ bung hiệu năng sức mạnh cho chiếc m&aacute;y t&iacute;nh của anh em, t&iacute;nh năng n&agrave;y c&oacute; trực tiếp tr&ecirc;n Windows. Tuy nhi&ecirc;n, một v&agrave;i h&atilde;ng c&ograve;n mang v&agrave;o phần mềm chuy&ecirc;n dụng để điều chỉnh hiệu quả hơn.</p>\r\n\r\n<p><img alt=\"tangtoclaptopgaming\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362832/tangtoclaptopgaming-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<h3><strong>4. Điều chỉnh tốc độ quạt</strong></h3>\r\n\r\n<p>Th&ocirc;ng thường c&aacute;c h&atilde;ng v&igrave; nhiệt độ sẽ để tốc độ quạt ở mức trung b&igrave;nh, điều n&agrave;y đồng nghĩa việc hiệu năng của m&aacute;y cũng nằm ở mức trung b&igrave;nh. Anh em c&oacute; thể tăng tốc laptop gaming bằng c&aacute;ch đẩy nhanh tốc độ quạt nhưng anh em sẽ đ&aacute;nh đổi về nhiệt độ.</p>\r\n\r\n<p><img alt=\"tangtoclaptopgaming\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362832/tangtoclaptopgaming2-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<h3><strong>5. Vừa sạc vừa chơi</strong></h3>\r\n\r\n<p>Nghe c&oacute; vẻ v&ocirc; l&yacute;, v&igrave; kh&ocirc;ng ai khuyến kh&iacute;ch vừa sạc, vừa sử dụng thiết bị điện tử bởi v&igrave; sẽ g&acirc;y chai pin. Tuy nhi&ecirc;n, đối với laptop th&igrave; điều n&agrave;y ho&agrave;n to&agrave;n ngược lại, bạn n&ecirc;n cắm sạc để m&aacute;y c&oacute; đủ điện năng sử dụng tối đa sức mạnh của phần cứng.</p>\r\n\r\n<p><img alt=\"tangtoclaptopgaming\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362832/tangtoclaptopgaming1-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<p>Anh em thấy những c&aacute;ch tr&ecirc;n đ&acirc;y như thế n&agrave;o? Nếu thấy b&agrave;i viết hữu &iacute;ch cho m&igrave;nh xin một like v&agrave; một share nh&eacute;. Rất cảm ơn anh em đ&atilde; xem b&agrave;i viết.</p>\r\n\r\n<p>Anh em c&oacute; thể tham khảo th&ecirc;m về chương tr&igrave;nh khuyến m&atilde;i laptop gaming bắt đầu từ&nbsp;<strong>1 th&aacute;ng 6 năm 2021 đến 30 th&aacute;ng 6 năm 2021.</strong></p>\r\n\r\n<p><strong><a href=\"https://www.thegioididong.com/laptop-gaming\" target=\"_blank\">CHƯƠNG TR&Igrave;NH KHUYẾN M&Atilde;I LAPTOP GAMING</a></strong></p>', '2021-06-25 09:14:26', '2021-06-26 10:35:11'),
(21, 'Hướng dẫn cách biến thanh Taskbar trên Windows 10 thành Windows 11, cực kỳ đơn giản chỉ với vài bước làm', 'image/ha8gbCVcylRhk3NhzArHgNT7y67F9WuRfbk9hAZS.jpg', '<h1>Hướng dẫn c&aacute;ch biến thanh Taskbar tr&ecirc;n Windows 10 th&agrave;nh Windows 11, cực kỳ đơn giản chỉ với v&agrave;i bước l&agrave;m</h1>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tin-tuc/profile/0\">Nguyễn Thang Khang</a>&nbsp;1 ng&agrave;y trước</p>\r\n\r\n<p><img alt=\"Taskbar\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362811/taskbar-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<h2>C&oacute; một c&aacute;ch đơn giản để&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/huong-dan-cach-bien-thanh-taskbar-tren-windows-10-thanh-windows-11-1362811\" target=\"_blank\">biến thanh Taskbar của Windows 10 th&agrave;nh Windows 11</a>&nbsp;m&agrave; kh&ocirc;ng cần phải c&agrave;i đặt lại chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\">m&aacute;y t&iacute;nh</a>&nbsp;của anh em. Với thanh Taskbar mới chiếc laptop của anh em sẽ sinh động hơn rất nhiều. H&atilde;y c&ugrave;ng m&igrave;nh xem l&agrave;m như thế n&agrave;o để thực hiện nh&eacute;.</h2>\r\n\r\n<h3><strong>Hướng dẫn mang thanh Taskbar tr&ecirc;n Windows 11 l&ecirc;n Windows</strong></h3>\r\n\r\n<p><strong>C&aacute;ch 1:&nbsp;</strong>Đầu ti&ecirc;n anh em&nbsp;<strong>phải chuột v&agrave;o thanh Taskbar</strong>&nbsp;v&agrave; mở kh&oacute;a bằng c&aacute;ch chọn v&agrave;o&nbsp;<strong>Lock the Taskbar</strong>.</p>\r\n\r\n<p><img alt=\"Đem thanh Taskbar trên Windows 11 lên Windows 10\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362811/demthanhtaskbartrenwindows11lenwindows101-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<p><strong>Bước 1:</strong>&nbsp;Anh em chọn tiếp v&agrave;o&nbsp;<strong>Toolbars&nbsp;</strong>v&agrave; chọn&nbsp;<strong>Links</strong>.</p>\r\n\r\n<p><img alt=\"Đem thanh Taskbar trên Windows 11 lên Windows 10\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362811/demthanhtaskbartrenwindows11lenwindows102-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<p><strong>Bước 2:&nbsp;</strong>Anh em nhấn giữ v&agrave; k&eacute;o thanh&nbsp;<strong>Links&nbsp;</strong>về b&ecirc;n tr&aacute;i hết cỡ.</p>\r\n\r\n<p><img alt=\"Đem thanh Taskbar trên Windows 11 lên Windows 10\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362811/demthanhtaskbartrenwindows11lenwindows103-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<p><strong>Bước 3:&nbsp;</strong>Cuối c&ugrave;ng anh em nhấn giữ biểu tượng&nbsp;<strong>2 gạch gần biểu tường Windows&nbsp;</strong>về b&ecirc;n phải, sao cho c&aacute;c ứng dụng ngay giữ l&agrave; xong nh&eacute;.</p>\r\n\r\n<p><img alt=\"Đem thanh Taskbar trên Windows 11 lên Windows 10\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362811/demthanhtaskbartrenwindows11lenwindows104-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<p><strong>C&aacute;ch 2:&nbsp;</strong>Anh em truy cập v&agrave;o đường link b&ecirc;n dưới để tải nh&agrave;nh phần mềm&nbsp;<strong>Taskbar X&nbsp;</strong>về m&aacute;y.</p>\r\n\r\n<p><a href=\"https://chrisandriessen.nl/taskbarx\" target=\"_blank\">Link truy cập</a>.</p>\r\n\r\n<p><img alt=\"Đem thanh Taskbar trên Windows 11 lên Windows 10\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362811/demthanhtaskbartrenwindows11lenwindows105-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<p>Sau đ&oacute;, anh em giải n&eacute;n phần mềm ra v&agrave; mở ứng dụng&nbsp;<strong>Taskbar X&nbsp;</strong>l&ecirc;n c&aacute;c icon sẽ tự chạy v&agrave;o giữa.</p>\r\n\r\n<p><strong>Lưu &yacute;:</strong>&nbsp;Anh em phải lu&ocirc;n mở kh&oacute;a Taskbar nh&eacute;.</p>\r\n\r\n<p><img alt=\"Đem thanh Taskbar trên Windows 11 lên Windows 10\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362811/demthanhtaskbartrenwindows11lenwindows10-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<p>Ngo&agrave;i ra, trong ứng dụng&nbsp;<strong>TaskbarX Configurator.</strong></p>\r\n\r\n<p><img alt=\"Đem thanh Taskbar trên Windows 11 lên Windows 10\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362811/demthanhtaskbartrenwindows11lenwindows106-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<p>C&oacute; rất nhiều t&iacute;nh năng th&uacute; vị như&nbsp;<strong>Style, m&agrave;u sắc</strong>... Anh em c&oacute; thể tinh chỉnh nhiều hơn.</p>\r\n\r\n<p><img alt=\"Đem thanh Taskbar trên Windows 11 lên Windows 10\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362811/demthanhtaskbartrenwindows11lenwindows107-_1366x768-800-resize.jpg\" /></p>\r\n\r\n<p>Anh em thấy thanh Taskbar mới n&agrave;y như thế n&agrave;o? Nếu thấy b&agrave;i viết hữu &iacute;ch cho m&igrave;nh xin một like v&agrave; một share nh&eacute;. Rất cảm ơn anh em đ&atilde; xem b&agrave;i viết.</p>', '2021-06-25 09:15:34', '2021-06-26 10:34:21'),
(22, 'Tính năng mới cực hot trên Windows 11: Chạy ứng dụng Android không cần giả lập, tha hồ tận hưởng game mobile', 'image/Z4X9KiCXoH7DTyh7xLllv5Sa440Ljj56qf9vpIJv.jpg', '<h1>T&iacute;nh năng mới cực hot tr&ecirc;n Windows 11: Chạy ứng dụng Android kh&ocirc;ng cần giả lập, tha hồ tận hưởng game mobile</h1>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tin-tuc/profile/0\">Hồ Sĩ Ho&agrave;ng</a>&nbsp;15 giờ trước&nbsp;5 b&igrave;nh luận</p>\r\n\r\n<p><img alt=\"Windows 11 có thể chạy ứng dụng Android\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1363116/win11_2_1280x720-800-resize.jpg\" /></p>\r\n\r\n<h2>Hệ điều h&agrave;nh Windows 11 đ&atilde; ch&iacute;nh thức được Microsoft ra mắt với thiết kế ho&agrave;n to&agrave;n mới c&ugrave;ng nhiều t&iacute;nh năng được cập nhật. Trong số đ&oacute; phải kể đến một t&iacute;nh năng m&agrave; ai y&ecirc;u th&iacute;ch game mobile sẽ rất th&iacute;ch l&agrave; Windows 11 nay c&oacute; thể chạy được ứng dụng Android m&agrave; kh&ocirc;ng cần giả lập.</h2>\r\n\r\n<p>Cụ thể, với Windows 11, Microsoft đ&atilde; hợp t&aacute;c với Amazon để đưa kho ứng dụng Amazon v&agrave;o trong Microsoft Store. Nhờ đ&oacute;, Windows 11 c&oacute; thể chạy c&aacute;c ứng dụng Android ngay tr&ecirc;n m&aacute;y m&agrave; kh&ocirc;ng cần ứng dụng giả lập. Việc của bạn cần l&agrave;m chỉ l&agrave; t&igrave;m v&agrave; c&agrave;i đặt ứng dụng tr&ecirc;n Microsoft Store.</p>\r\n\r\n<p><img alt=\"Windows 11 có thể chạy ứng dụng Android\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1363116/win11_4_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>Với việc hỗ trợ c&aacute;c ứng dụng Android, bạn ho&agrave;n to&agrave;n c&oacute; thể chạy c&aacute;c ứng dụng Android nổi tiếng như TikTok ngay tr&ecirc;n m&aacute;y t&iacute;nh c&agrave;i Windows 11. Bạn c&oacute; thể d&ugrave;ng c&aacute;c thao t&aacute;c cảm ứng của bạn với app như khi d&ugrave;ng tr&ecirc;n&nbsp;<a href=\"http://thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>.</p>\r\n\r\n<p>Từ nay việc quay v&agrave; đăng tải video l&ecirc;n TikTok ngay tr&ecirc;n m&aacute;y t&iacute;nh l&agrave; ho&agrave;n to&agrave;n c&oacute; thể v&agrave; trở n&ecirc;n dễ d&agrave;ng hơn với c&aacute;c nh&agrave; s&aacute;ng tạo nội dung.</p>\r\n\r\n<p><img alt=\"Windows 11 có thể chạy cả TikTok\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1363116/win11_5_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>Tuy nhi&ecirc;n, hiện tại Windows 11 chỉ giới hạn c&agrave;i đặt một số ứng dụng đ&atilde; c&oacute; sẵn tr&ecirc;n kho ứng dụng của Amazon.</p>\r\n\r\n<p>Bạn nghĩ g&igrave; về Windows 11 c&oacute; thể chạy ứng dụng Android?</p>\r\n\r\n<p>Nguồn:&nbsp;<a href=\"https://mspoweruser.com/microsoft-android-apps-are-coming-to-windows-11/\" target=\"_blank\">MSPoweruser</a></p>\r\n\r\n<p>Bi&ecirc;n tập bởi L&ecirc; Hải Nam</p>', '2021-06-25 09:22:26', '2021-06-26 10:29:58'),
(23, 'Lenovo IdeaPad 5i và IdeaPad Flex 5i ra mắt: Chromebook mới dùng chip Intel Tiger Lake thế hệ 11, giá từ 10.1 triệu đồng', 'image/Qx8t8Wt2jQqEjtj94sPQE7iCRz3OA7kpsZj0jJfW.jpg', '<h1>Lenovo IdeaPad 5i v&agrave; IdeaPad Flex 5i ra mắt: Chromebook mới d&ugrave;ng chip Intel Tiger Lake thế hệ 11, gi&aacute; từ 10.1 triệu đồng</h1>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tin-tuc/profile/1996269\"><img src=\"https://cdn.tgdd.vn/UserUpload/Social2019/IMG_1697_2NDE3F.jpg\" style=\"height:20px; width:20px\" />Dương L&ecirc;</a>&nbsp;1 ng&agrave;y trước</p>\r\n\r\n<p><img alt=\"Lenovo IdeaPad 5i và IdeaPad Flex 5i ra mắt: Chromebook mới dùng chip Intel Tiger Lake thế hệ 11, giá từ 10.1 triệu đồng\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362866/lenovo-tiger-lake-5i_1280x720-800-resize.jpg\" /></p>\r\n\r\n<h2>Trước thềm&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/su-kien/mwc-barcelona-2021-589\" target=\"_blank\">Mobile World Congress 2021</a>,&nbsp;<a href=\"https://www.thegioididong.com/laptop-lenovo\" target=\"_blank\">Lenovo</a>&nbsp;đ&atilde; c&ocirc;ng bố hai chiếc Chromebook mới&nbsp;với t&ecirc;n gọi IdeaPad 5i v&agrave; IdeaPad Flex 5i. Cả hai mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\">laptop</a>&nbsp;n&agrave;y đều được trang bị bộ vi xử l&yacute; Tiger Lake thế hệ thứ 11 của Intel.</h2>\r\n\r\n<h3><strong>Cấu h&igrave;nh v&agrave; gi&aacute; b&aacute;n&nbsp;Lenovo IdeaPad 5i</strong></h3>\r\n\r\n<p>Lenovo IdeaPad 5i c&oacute; m&agrave;n h&igrave;nh Full HD 14 inch c&ugrave;ng t&ugrave;y chọn phi&ecirc;n bản cảm ứng. T&ugrave;y thuộc v&agrave;o cấu h&igrave;nh m&agrave; Chromebook d&ugrave;ng CPU Intel Core i5-1135G7, Intel Core i3-1115G4 hoặc Pentium 7505. Laptop c&oacute; RAM 4 GB hoặc 8 GB v&agrave; ổ cứng SSD l&ecirc;n đến 512 GB.</p>\r\n\r\n<p><img alt=\"Lenovo IdeaPad 5i\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362866/lenovo-5i-chromebook_1280x640-800-resize.jpg\" /></p>\r\n\r\n<p>Về kết nối, Lenovo IdeaPad 5i được t&iacute;ch hợp&nbsp;hai cổng USB-C 3.1, một cổng USB-A 3.1, khe cắm thẻ nhớ&nbsp;<a href=\"https://www.thegioididong.com/the-nho-dien-thoai\" target=\"_blank\">microSD</a>,&nbsp;cổng &acirc;m thanh kết hợp với micr&ocirc;, hỗ trợ Bluetooth 5.1 v&agrave; Wi-Fi 6.</p>\r\n\r\n<p>Laptop c&oacute; t&ugrave;y chọn m&agrave;u Sand v&agrave; Storm Grey. Cả hai phi&ecirc;n bản đều c&oacute; một thanh &aacute;nh s&aacute;ng b&ecirc;n dưới trackpad. Thanh đ&egrave;n n&agrave;y sẽ chuyển đổi giữa c&aacute;c m&agrave;u để hiển thị thời lượng pin c&ograve;n lại.</p>\r\n\r\n<p><img alt=\"Lenovo IdeaPad 5i\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362866/lenovo-5i_1280x640-800-resize.jpg\" /></p>\r\n\r\n<p>Lenovo IdeaPad 5i sẽ được mở b&aacute;n từ th&aacute;ng 7 với gi&aacute; khởi điểm 439.99 USD (khoảng 10.1 triệu đồng).</p>\r\n\r\n<h3><strong>Cấu h&igrave;nh v&agrave; gi&aacute; b&aacute;n Lenovo IdeaPad Flex 5i</strong></h3>\r\n\r\n<p>Nếu muốn mua thiết bị 2 trong 1, th&igrave; IdeaPad Flex 5i l&agrave; d&agrave;nh cho bạn. M&aacute;y c&oacute; m&agrave;n h&igrave;nh cảm ứng 13.3 inch với t&ugrave;y chọn tấm nền OLED, d&ugrave;ng&nbsp;c&aacute;c bộ xử l&yacute; tương tự như IdeaPad 5i hoặc t&ugrave;y chọn Celeron 6305.</p>\r\n\r\n<p><img alt=\"Lenovo IdeaPad Flex 5i\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362866/lenovo-flex-5i-chromebook_1280x640-800-resize.jpg\" /></p>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;Lenovo IdeaPad Flex 5i c&ograve;n c&oacute; RAM 4 GB hoặc 8GB, bộ nhớ&nbsp;eMMC 32 GB hoặc 64 GB, SSD 128 GB, 256 GB hoặc 512 GB.</p>\r\n\r\n<p><img alt=\"Lenovo IdeaPad Flex 5i\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362866/lenovo-flex-5i-chromebook-13inch_1280x640-800-resize.jpg\" /></p>\r\n\r\n<p>Lenovo IdeaPad Flex 5i cũng hỗ trợ c&aacute;c kết nối tương tự như IdeaPad 5i, c&oacute; hai m&agrave;u Abyss Blue v&agrave; Iron Grey với gi&aacute; khởi điểm 439.99 USD (khoảng 10.1 triệu đồng).</p>\r\n\r\n<p>Bạn nhận x&eacute;t thế n&agrave;o về thiết kế, cấu h&igrave;nh v&agrave; gi&aacute; b&aacute;n của&nbsp;Lenovo IdeaPad 5i, IdeaPad Flex 5i?</p>\r\n\r\n<p>Nguồn:&nbsp;<a href=\"https://www.gizmochina.com/2021/06/23/lenovos-ideapad-5i-has-a-light-bar-convertible-ideapad-flex-5i-tags-along-too/\" target=\"_blank\">Gizmochina</a></p>', '2021-06-25 09:24:33', '2021-06-26 10:29:17'),
(24, 'Lenovo ra mắt ThinkPad X1 Extreme Gen 4: Flagship của giới laptop, CPU lên đến Intel Core i9 vPro Gen 11, cạnh tranh với XPS', 'image/TTDLXN7FsOFwV0eSbNhmSEuSqmp3yjJ0V29mDszm.jpg', '<h1>Lenovo ra mắt ThinkPad X1 Extreme Gen 4: Flagship của giới laptop, CPU l&ecirc;n đến Intel Core i9 vPro Gen 11, cạnh tranh với XPS</h1>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tin-tuc/profile/5755350\"><img src=\"https://graph.facebook.com/1456497354672508/picture\" style=\"height:20px; width:20px\" />Minh Vũ</a>&nbsp;1 ng&agrave;y trước</p>\r\n\r\n<p><img alt=\"ThinkPad X1 Extreme Gen 4\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362847/thinkpad-x1-extreme-gen-4-1_1280x720-800-resize.jpg\" /></p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/laptop-dell\" target=\"_blank\">Dell</a>&nbsp;đ&atilde; tung ra thị trường những mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop-dell-xps\" target=\"_blank\">XPS</a>&nbsp;được thiết kế lại, nhỏ gọn mang t&iacute;nh di động cao, đồng thời được n&acirc;ng cấp đ&aacute;ng kể về hiệu năng.&nbsp;<a href=\"https://www.thegioididong.com/laptop-lenovo\" target=\"_blank\">Lenovo</a>&nbsp;cũng kh&ocirc;ng nằm ngo&agrave;i cuộc chơi khi vừa cho ra mắt ThinkPad X1 Extreme Gen 4, chiếc flagship đ&uacute;ng nghĩa trong thế giới&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\">laptop</a>.</h2>\r\n\r\n<p>Cũng giống như Dell, Lenovo đ&atilde; thay đổi k&iacute;ch thước m&agrave;n h&igrave;nh th&agrave;nh tỷ lệ 16:10 tr&ecirc;n chiếc ThinkPad mới nhất. Đ&oacute; l&agrave; m&agrave;n h&igrave;nh 16 inch độ ph&acirc;n giải WQXGA (2.560 x 1.600 px), c&oacute; bản l&ecirc;n đến 4K UHD+ (3.840 x 2.400 px) t&ugrave;y người d&ugrave;ng lựa chọn.</p>\r\n\r\n<p><img alt=\"ThinkPad X1 Extreme Gen 4\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362847/thinkpad-x1-extreme-gen-4-3_1280x653-800-resize.jpg\" /></p>\r\n\r\n<p>Để cạnh tranh với Dell XPS 16, Lenovo đ&atilde; t&iacute;ch hợp bộ xử l&yacute; đồ họa mạnh mẽ của NVIDIA, cấu h&igrave;nh t&ugrave;y bản từ NVIDIA RTX 3050 Ti đến RTX 3080 Max-Q. Việc t&iacute;ch hợp một bộ xử l&yacute; đồ họa khủng như RTX 3080 Max-Q đ&atilde; khiến Lonovo phải thiết kế lại hệ thống l&agrave;m m&aacute;t, cải thiện quạt tản nhiệt, khe tho&aacute;t kh&iacute;,...</p>\r\n\r\n<p>Mặc thiết kế lại &quot;nội thất&quot;, nhưng Lenovo vẫn giữ lại c&aacute;c cổng USB-A v&agrave; cổng HDMI tr&ecirc;n&nbsp;ThinkPad X1 Extreme Gen 4. Điểm mới của m&aacute;y l&agrave; loa được thiết kế ở cạnh b&agrave;n ph&iacute;m, b&agrave;n di chuột lớn hơn, cảm biến v&acirc;n tay được t&iacute;ch hợp chung với n&uacute;t nguồn v&agrave; bề mặt m&agrave;u đen nh&aacute;m c&oacute; khả năng chống b&aacute;m dấu v&acirc;n tay.</p>\r\n\r\n<p><img alt=\"ThinkPad X1 Extreme Gen 4\" src=\"https://cdn.tgdd.vn/Files/2021/06/24/1362847/thinkpad-x1-extreme-gen-4-5_1280x717-800-resize.jpg\" /></p>\r\n\r\n<p>ThinkPad X1 Extreme Gen 4 c&oacute; h&agrave;nh tr&igrave;nh b&agrave;n ph&iacute;m giảm xuống c&ograve;n 1.5 mm, dung lượng pin cũng được n&acirc;ng cấp so với người tiền nhiệm, từ 80 l&ecirc;n 90 Wh.</p>\r\n\r\n<p>Bộ vi xử l&yacute; của ThinkPad X1 Extreme Gen 4 l&ecirc;n đến Intel Core i9 vPro thế hệ 11, đồng thời c&oacute; hai khe cắm M.2 2280, hai khe SO-DIMM cũng như hỗ trợ 5G, Wi-Fi 6e v&agrave; một webcam 1080p.</p>\r\n\r\n<p>ThinkPad X1 Extreme Gen 4 sẽ được l&ecirc;n kệ v&agrave;o th&aacute;ng 8/2021, với mức gi&aacute; 2.100 Euro (57.7 triệu đồng).</p>\r\n\r\n<p>Bạn th&iacute;ch&nbsp;ThinkPad X1 Extreme Gen 4 chứ?</p>\r\n\r\n<p>Nguồn:&nbsp;<a href=\"https://www.notebookcheck.net/ThinkPad-X1-Extreme-Gen-4-Lenovo-s-new-multimedia-flagship-attacks-Dell-XPS-15-17.547124.0.html\" target=\"_blank\">Notebookcheck</a></p>', '2021-06-25 09:27:55', '2021-06-26 10:19:10'),
(25, 'Trên tay ASUS ExpertBook B9 (B9400CE): Thiết kế gọn nhẹ, khối lượng chỉ 1 kg nhưng lại sở hữu hiệu năng mạnh mẽ', 'image/mlpzyMlfIaa7p0OYNeiPWQvZKBG9ufU6oYz9YKTC.jpg', '<h2><a href=\"https://www.thegioididong.com/laptop-asus\" target=\"_blank\">ASUS</a>&nbsp;vốn dĩ l&agrave; một trong những thương hiệu hay được biết đến với những d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop-gaming\" target=\"_blank\">laptop chơi game</a>&nbsp;v&agrave; d&ograve;ng laptop phổ th&ocirc;ng d&agrave;nh cho giới trẻ. Tuy nhi&ecirc;n, với c&aacute;c đối tượng kh&aacute;ch h&agrave;ng l&agrave; doanh nh&acirc;n/doanh nghiệp nhỏ th&igrave; họ lại cần những chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\">laptop</a>&nbsp;mỏng nhẹ, c&oacute; thời lượng sử dụng pin l&acirc;u d&agrave;i v&agrave; đ&oacute; ch&iacute;nh l&agrave; l&yacute; do v&igrave; sao m&agrave;&nbsp;<a href=\"https://www.thegioididong.com/laptop-asus-expertbook\" target=\"_blank\">d&ograve;ng laptop ASUS ExpertBook</a>&nbsp;ra đời. Trong b&agrave;i viết ng&agrave;y h&ocirc;m nay, h&atilde;y c&ugrave;ng m&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/tren-tay-danh-gia-asus-expertbook-b9-1362077\" target=\"_blank\">tr&ecirc;n tay ASUS ExpertBook B9 (B9400)</a>&nbsp;để xem em n&oacute; c&oacute; g&igrave; đặc biệt nh&eacute;!</h2>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/tren-tay-asus-rog-zephyrus-duo-2021-1355315\" target=\"_blank\">Tr&ecirc;n tay ASUS ROG Zephyrus Duo (2021): M&agrave;n h&igrave;nh k&eacute;p v&agrave; cấu h&igrave;nh khủng</a></p>\r\n\r\n<h3><strong>Bộ phụ kiện độc đ&aacute;o của ASUS ExpertBook B9</strong></h3>\r\n\r\n<p>Trước khi đi v&agrave;o nh&acirc;n vật ch&iacute;nh th&igrave; m&igrave;nh sẽ d&agrave;nh một phần ri&ecirc;ng để n&oacute;i về những m&oacute;n phụ kiện b&ecirc;n trong hộp của ASUS ExpertBook B9. Đầu ti&ecirc;n ch&iacute;nh l&agrave; chiếc bao đựng với họa tiết phay xước độc đ&aacute;o m&agrave; ASUS đ&atilde; tặng k&egrave;m với sản phẩm. M&igrave;nh phải c&ocirc;ng nhận rằng m&oacute;n phụ kiện n&agrave;y được ho&agrave;n thiện cực kỳ tốt, từng đường kim mũi chỉ đều được gia c&ocirc;ng tỉ mỉ. Đặc biệt l&agrave; b&ecirc;n trong bao đựng n&agrave;y c&ograve;n c&oacute; 1 lớp nhung cực kỳ mềm mại, gi&uacute;p bảo vệ chiếc laptop được tốt hơn.</p>\r\n\r\n<p><img alt=\"Bao đựng được tặng kèm với ASUS ExpertBook B9 có chất lượng tương đối tốt.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/asus-expertbook-b9-bao-dung_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>Bao đựng được tặng k&egrave;m với ASUS ExpertBook B9 c&oacute; chất lượng tương đối tốt.</p>\r\n\r\n<p><img alt=\"Lớp nhung mềm mại bên trong bao đựng này sẽ bảo vệ laptop tốt hơn.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/asus-expertbook-b9-bao-dung-ben-trong_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>Lớp nhung mềm mại b&ecirc;n trong bao đựng n&agrave;y sẽ bảo vệ laptop tốt hơn.</p>\r\n\r\n<p><img alt=\"Dây cáp kết nối của ASUS ExpertBook B9.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/sac-asus-expertbook-b9_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>D&acirc;y sạc của ASUS ExpertBook B9.</p>\r\n\r\n<p><img alt=\"Dây cáp kết nối của ASUS ExpertBook B9.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/sac-asus-expertbook-b9-1_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>D&acirc;y sạc của ASUS ExpertBook B9.</p>\r\n\r\n<p>B&ecirc;n cạnh bộ c&aacute;p sạc, c&aacute;p chuyển đổi Type-C sang cổng Ethernet th&igrave; phần hộp đựng của hai m&oacute;n phụ kiện n&agrave;y c&ograve;n được d&ugrave;ng l&agrave;m gi&aacute; đỡ cho laptop ASUS Expert Book B9. M&igrave;nh phải c&ocirc;ng nhận đ&acirc;y l&agrave; một động th&aacute;i bảo vệ m&ocirc;i trường rất hay đến từ ASUS.</p>\r\n\r\n<p><img alt=\"Hộp đựng phụ kiện của ASUS ExpertBook B9 có thể chuyển thành giá đỡ laptop. \" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/sac-asus-expertbook-b9_1280x510-800-resize.png\" /></p>\r\n\r\n<p>Hộp đựng phụ kiện của ASUS ExpertBook B9 c&oacute; thể chuyển th&agrave;nh gi&aacute; đỡ laptop. (Nguồn: ASUS).</p>\r\n\r\n<p>M&igrave;nh c&oacute; trải nghiệm thử th&igrave; thấy gi&aacute; đỡ laptop n&agrave;y tương đối cứng c&aacute;p mặc d&ugrave; chỉ được l&agrave;m từ giấy b&igrave;a t&aacute;i chế. Khi m&igrave;nh đặt chiếc ASUS ExpertBook l&ecirc;n th&igrave; cũng kh&ocirc;ng c&oacute; hiện tượng bị x&ecirc; dịch, bởi v&igrave; ở mặt đ&aacute;y c&oacute; hai miếng d&aacute;n chống trơn trượt kh&aacute; chắc chắn.</p>\r\n\r\n<p><img alt=\"Các bạn nhìn xem, giá đỡ laptop bằng giấy bìa tái chế này cũng xịn đấy chứ.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/asus-expertbook-b9-gia-do-laptop_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>C&aacute;c bạn nh&igrave;n xem, gi&aacute; đỡ laptop bằng giấy b&igrave;a t&aacute;i chế n&agrave;y cũng xịn đấy chứ.</p>\r\n\r\n<p><img alt=\"Ở mặt đáy của giá đỡ này là hai miếng dán xốp gia tăng độ bám, tránh bị trơn trượt khi chúng ta để ở mặt bàn.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/gia-do-hai-mieng-dan-xop-1_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>Ở mặt đ&aacute;y của gi&aacute; đỡ n&agrave;y l&agrave; hai miếng d&aacute;n xốp gia tăng độ b&aacute;m, tr&aacute;nh bị trơn trượt khi ch&uacute;ng ta để ở mặt b&agrave;n.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, m&igrave;nh hơi tiếc một ch&uacute;t v&igrave; phụ kiện gi&aacute; đỡ n&agrave;y lại che mất phần tản nhiệt của ASUS ExpertBook B9 n&ecirc;n trong qu&aacute; tr&igrave;nh sử dụng th&igrave; m&igrave;nh thấy m&aacute;y n&oacute;ng l&ecirc;n tương đối nhiều. C&aacute;c bạn c&oacute; thể xem clip minh họa ở b&ecirc;n dưới.</p>\r\n\r\n<p><img alt=\"Phần giá đỡ này lại vô tình che mất khe tản nhiệt của ASUS ExpertBook B9.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/gia-do-che-khe-tan-nhiet_600x338.gif\" /></p>\r\n\r\n<p>Phần gi&aacute; đỡ n&agrave;y lại v&ocirc; t&igrave;nh che mất khe tản nhiệt của ASUS ExpertBook B9.</p>\r\n\r\n<h3><strong>Thiết kế của ASUS ExpertBook B9: Laptop mỏng nhẹ nhưng bền bỉ</strong></h3>\r\n\r\n<p>B&acirc;y giờ th&igrave; ch&uacute;ng ta c&ugrave;ng đến với nh&acirc;n vật ch&iacute;nh, chiếc ASUS ExpertBook B9. Điều đầu ti&ecirc;n m&agrave; m&igrave;nh ấn tượng về mẫu laptop n&agrave;y đ&oacute; l&agrave; em n&oacute; qu&aacute; nhẹ lu&ocirc;n, khối lượng tổng thể chỉ v&agrave;o khoảng 1 kg v&agrave; m&igrave;nh ho&agrave;n to&agrave;n c&oacute; thể cầm chiếc laptop n&agrave;y bằng một tay. C&oacute; được điều n&agrave;y l&agrave; do ASUS ExpertBook B9 được trang bị bộ khung&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/hop-kim-lithium-magie-tren-laptop-la-gi-1269298\" target=\"_blank\">hợp kim lithium-magi&ecirc;</a>&nbsp;nhẹ hơn 16% so với người tiền nhiệm.</p>\r\n\r\n<p><img alt=\"Phần khung của ExpertBook B9 được làm từ hợp kim lithium-magie, nhờ đó mà khối lượng máy chỉ 1kg.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/asus-expertbook-b9-khung-hop-kim_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>Phần khung của ExpertBook B9 được l&agrave;m từ hợp kim lithium-magie, nhờ đ&oacute; m&agrave; khối lượng m&aacute;y chỉ 1kg.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, ASUS cũng sử dụng quy tr&igrave;nh cắt CNC với độ ch&iacute;nh x&aacute;c cao, hạn chế những chi tiết thừa v&agrave; giảm thiểu những lỗ rỗng kh&ocirc;ng cần thiết b&ecirc;n trong. Nhờ v&agrave;o đ&oacute; m&agrave; to&agrave;n bộ khung của ASUS ExpertBook B9 cứng c&aacute;p v&agrave; khỏe hơn so với nhưng g&igrave; đạt được bằng phương ph&aacute;p rập khu&ocirc;n kim loại truyền thống.</p>\r\n\r\n<p><img alt=\"Nhờ quy trình cắt CNC kỹ lưỡng, phần khung của ASUS ExpertBook B9 có độ bền đạt chuẩn quân đội Mỹ.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/asus-expertbook-b9-do-ben_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>Nhờ quy tr&igrave;nh cắt CNC kỹ lưỡng, phần khung của ASUS ExpertBook B9 c&oacute; độ bền đạt chuẩn qu&acirc;n đội Mỹ.</p>\r\n\r\n<p>Chưa hết, ASUS ExpertBook B9 c&ograve;n c&oacute; độ bền đạt chứng nhận chuẩn qu&acirc;n đội Hoa Kỳ MIL-STD 810H cực kỳ xịn s&ograve; đ&oacute; nha c&aacute;c bạn. Thiết bị cũng đ&atilde; trải qua những cuộc thử nghiệm nội bộ nghi&ecirc;m ngặt bao gồm kiểm tra &aacute;p lực tấm nền, thử nghiệm thả rơi v&agrave; va đập để đảm bảo độ bền. V&igrave; vậy, ch&uacute;ng ta sẽ kh&ocirc;ng cần phải lo về c&aacute;c c&uacute; va đập hay v&ocirc; t&igrave;nh l&agrave;m rơi m&aacute;y trong qu&aacute; tr&igrave;nh sử dụng h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p><img alt=\"Cạnh trái của máy là nơi đặt 2 cổng USB-C Thunderbolt 4, 1 cổng HDMI và 1 cổng micro HDMI.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/asus-expertbook-b9-gia-canh-trai_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>Cạnh tr&aacute;i của m&aacute;y l&agrave; nơi đặt 2 cổng USB-C Thunderbolt 4, 1 cổng HDMI v&agrave; 1 cổng micro HDMI.</p>\r\n\r\n<p><img alt=\"Ở cạnh phải, chúng ta sẽ có cổng 3.5 mm, 1 cổng USB-A 3.2 Gen2 và khe khóa Kensington.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/asus-expertbook-b9-gia-canh-phai_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>Ở cạnh phải, ch&uacute;ng ta sẽ c&oacute; cổng 3.5 mm, 1 cổng USB-A 3.2 Gen2 v&agrave; khe kh&oacute;a Kensington.</p>\r\n\r\n<p><img alt=\"Mặt đáy của thiết bị sẽ là nơi đặt khe tản nhiệt, hai loa ở cạnh bên trái/phải và 4 chân cao su tạo độ bám cho chiếc laptop.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/asus-expertbook-b9-mat-sau_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>Mặt đ&aacute;y của thiết bị sẽ l&agrave; nơi đặt khe tản nhiệt, hai loa ở cạnh b&ecirc;n tr&aacute;i/phải v&agrave; 4 ch&acirc;n cao su tạo độ b&aacute;m cho chiếc laptop.</p>\r\n\r\n<p>Mở ra b&ecirc;n trong, m&igrave;nh cảm thấy kh&aacute; bất ngờ với độ mỏng 4 mm của phần viền m&agrave;n h&igrave;nh tr&ecirc;n ASUS ExpertBook. Thiết bị được trang bị m&agrave;n h&igrave;nh c&oacute; k&iacute;ch thước l&agrave; 14 inch c&ugrave;ng tấm nền chống l&oacute;a giảm h&igrave;nh ảnh phản chiếu kh&ocirc;ng mong muốn, m&igrave;nh c&oacute; thể d&ugrave;ng m&aacute;y trong m&ocirc;i trường nhiều &aacute;nh s&aacute;ng v&agrave; giảm căng thẳng cho mắt sau nhiều giờ sử dụng. Đặc biệt l&agrave; phần cạnh tr&ecirc;n của m&agrave;n h&igrave;nh c&ograve;n c&oacute; ph&iacute;m gạt để che webcam, gi&uacute;p m&igrave;nh bảo mật quyền ri&ecirc;ng tư c&aacute; nh&acirc;n được tốt hơn.</p>\r\n\r\n<p><img alt=\"ASUS ExpertBook B9 có viền màn hình mỏng chỉ 4 mm. \" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/asus-expertbook-b9-man-hinh-mong_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>ASUS ExpertBook B9 c&oacute; viền m&agrave;n h&igrave;nh mỏng chỉ 4 mm.</p>\r\n\r\n<p><img alt=\"Ở viền trên của màn hình này còn có phím gạt dùng để che webcam, gia tăng quyền riêng tư cho người dùng.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/asus-expertbook-b9-che-webcam_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>Ở viền tr&ecirc;n của m&agrave;n h&igrave;nh n&agrave;y c&ograve;n c&oacute; ph&iacute;m gạt d&ugrave;ng để che webcam, gia tăng quyền ri&ecirc;ng tư cho người d&ugrave;ng.</p>\r\n\r\n<p>Ngay ở ph&iacute;a dưới l&agrave; phần b&agrave;n ph&iacute;m của ASUS ExpertBook B9 được thiết kế theo dạng b&agrave;n ph&iacute;m chiclet (c&aacute;c ph&iacute;m bấm được thiết kế hơi l&otilde;m v&agrave;o trung t&acirc;m, khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m cũng được gia tăng). Nhờ v&agrave;o đ&oacute; m&agrave; cảm gi&aacute;c đ&aacute;nh m&aacute;y l&agrave; kh&aacute; &ecirc;m, ph&iacute;m bấm c&oacute; độ đ&agrave;n hồi tốt với h&agrave;nh tr&igrave;nh ph&iacute;m l&agrave; 1.5 mm. Ngo&agrave;i ra, phần bản lề ErgoLift của chiếc laptop n&agrave;y cũng tự động nghi&ecirc;ng b&agrave;n ph&iacute;m l&ecirc;n một g&oacute;c cao hơn để m&igrave;nh c&oacute; vị tr&iacute; g&otilde; thoải m&aacute;i nhất.</p>\r\n\r\n<p>B&agrave;n ph&iacute;m của ASUS ExpertBook B9 cho cảm gi&aacute;c đ&aacute;nh m&aacute;y thoải m&aacute;i, ph&iacute;m bấm đ&agrave;n hồi tốt.</p>\r\n\r\n<p>Phần bản lề ErgoLift của thiết bị gi&uacute;p b&agrave;n ph&iacute;m nghi&ecirc;ng l&ecirc;n một ch&uacute;t, tạo vị tr&iacute; để g&otilde; m&aacute;y tốt hơn.</p>\r\n\r\n<p>Đ&atilde; n&oacute;i đến b&agrave;n ph&iacute;m th&igrave; m&igrave;nh kh&ocirc;ng thể n&agrave;o kh&ocirc;ng nhắc đến phần TouchPad c&oacute; thể chuyển đổi th&agrave;nh b&agrave;n ph&iacute;m số cực kỳ tiện lợi. M&igrave;nh chỉ cần nhấn v&agrave;o biểu tượng NumberPad ở ph&iacute;a tr&ecirc;n b&ecirc;n phải b&agrave;n di chuột để bật v&agrave; nhấn v&agrave;o biểu tượng ở b&ecirc;n tr&aacute;i để chuyển đổi giữa hai chế độ thiết lập độ s&aacute;ng cho b&agrave;n ph&iacute;m số. Phần TouchPad n&agrave;y cũng được l&agrave;m rộng r&atilde;i gi&uacute;p m&igrave;nh c&oacute; th&ecirc;m kh&ocirc;ng gian để thao t&aacute;c với thiết bị.</p>\r\n\r\n<p><img alt=\"Phần TouchPad của thiết bị còn có thể biến được thành bàn phím số đèn LED thông qua phím bấm ở góc trên bên phải.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/asus-expertbook-b9-touchpad_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>Phần TouchPad của thiết bị c&ograve;n c&oacute; thể biến được th&agrave;nh b&agrave;n ph&iacute;m số đ&egrave;n LED th&ocirc;ng qua ph&iacute;m bấm ở g&oacute;c tr&ecirc;n b&ecirc;n phải. (Nguồn: ASUS).</p>\r\n\r\n<h3><strong>ASUS ExpertBook B9 sở hữu hiệu năng mạnh mẽ c&ugrave;ng thời lượng pin l&acirc;u d&agrave;i</strong></h3>\r\n\r\n<p>Mặc d&ugrave; mang trong m&igrave;nh th&acirc;n h&igrave;nh nhỏ gọn như vậy th&ocirc;i chứ ASUS ExpertBook lại sở hữu hiệu năng mạnh mẽ lắm đ&oacute; nha. Th&ocirc;ng số cụ thể của chiếc laptop n&agrave;y l&agrave; như sau:</p>\r\n\r\n<ul>\r\n	<li>CPU: Intel Core i7-1165G7 (thế hệ thứ 11).</li>\r\n	<li>GPU: Intel Iris Xe Graphics.</li>\r\n	<li>RAM: 16 GB LPDDR4X c&oacute; sẵn (hỗ trợ mở rộng l&ecirc;n đến 32 GB).</li>\r\n	<li>Bộ nhớ trong: SSD 1TB M.2 NVMe PCIe 3.0.</li>\r\n	<li>Hệ điều h&agrave;nh: Windows 10 Home.</li>\r\n	<li>Dung lượng pin: 66 WHr, Li-ion 4 pin.</li>\r\n</ul>\r\n\r\n<p><img alt=\"ASUS ExpertBook B9 dù có thân hình mỏng manh nhưng hiệu năng lại vô cùng mạnh mẽ. \" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/asus-expertbook-b9-cau-hinh_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>ASUS ExpertBook B9 d&ugrave; c&oacute; th&acirc;n h&igrave;nh mỏng manh nhưng hiệu năng lại v&ocirc; c&ugrave;ng mạnh mẽ.</p>\r\n\r\n<p>M&igrave;nh tin với cấu h&igrave;nh n&agrave;y th&igrave; ASUS ExpertBook B9 sẽ dư sức đ&aacute;p ứng nhiều t&aacute;c vụ văn ph&ograve;ng nhẹ nh&agrave;ng cho đến c&aacute;c t&aacute;c vụ nặng hơn về đồ họa, đặc biệt l&agrave; với đối tượng kh&aacute;ch h&agrave;ng doanh nh&acirc;n/doanh nghiệp nhỏ với khối lượng c&ocirc;ng việc d&agrave;y đặc. M&igrave;nh c&oacute; trải nghiệm qua một số t&aacute;c vụ th&ocirc;ng thường v&agrave; edit h&igrave;nh ảnh bằng Photoshop tr&ecirc;n chiếc laptop n&agrave;y th&igrave; mọi thứ đều được xử l&yacute; mượt m&agrave;, ổn định.</p>\r\n\r\n<p><img alt=\"Viên pin của chiếc laptop này hoàn toàn có thể đáp ứng nhu cầu sử dụng cả ngày dài, máy còn hỗ trợ sạc nhanh nữa.\" src=\"https://cdn.tgdd.vn/Files/2021/06/21/1362077/asus-expertbook-b9-pin_1280x720-800-resize.jpg\" /></p>\r\n\r\n<p>Vi&ecirc;n pin của chiếc laptop n&agrave;y ho&agrave;n to&agrave;n c&oacute; thể đ&aacute;p ứng nhu cầu sử dụng cả ng&agrave;y d&agrave;i, m&aacute;y c&ograve;n hỗ trợ sạc nhanh nữa.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, dung lượng pin 66 WHr của ASUS ExpertBook B9 cũng sẽ phục vụ tốt nhu cầu sử dụng trong cả ng&agrave;y d&agrave;i của người d&ugrave;ng. Theo như ASUS c&ocirc;ng bố th&igrave; m&aacute;y c&oacute; thời lượng pin đủ cho cả ng&agrave;y trong một lần sạc, được bổ trợ bởi c&ocirc;ng nghệ tự l&agrave;m mới tấm nền tiết kiệm điện năng. Đồng thời, c&ocirc;ng nghệ sạc nhanh cho ph&eacute;p ch&uacute;ng ta sạc được 60% pin chỉ trong hơn 30 ph&uacute;t, m&igrave;nh đ&atilde; thử nghiệm khi hệ thống đang tắt/ở chế độ chờ.</p>\r\n\r\n<h3><strong>Tổng kết</strong></h3>\r\n\r\n<p>Vừa rồi l&agrave; những cảm nhận đầu ti&ecirc;n của m&igrave;nh sau khi tr&ecirc;n tay ASUS ExpertBook B9 (B9400CE). C&aacute; nh&acirc;n m&igrave;nh nghĩ chiếc laptop n&agrave;y c&ograve;n rất nhiều điểm th&uacute; vị kh&aacute;c m&agrave; m&igrave;nh chưa kh&aacute;m ph&aacute; ra, c&oacute; lẽ m&igrave;nh sẽ để d&agrave;nh trong b&agrave;i đ&aacute;nh gi&aacute; chi tiết về em n&oacute;. Vậy c&aacute;c bạn nghĩ như thế n&agrave;o về ASUS ExpertBook B9? Nếu c&oacute; th&igrave; đừng qu&ecirc;n b&igrave;nh luận ở b&ecirc;n dưới b&agrave;i viết cho m&igrave;nh biết với nh&eacute;!</p>', '2021-06-25 09:29:34', '2021-06-26 10:18:25');
INSERT INTO `tintuc` (`id`, `tieu_de`, `hinh_anh`, `noi_dung`, `created_at`, `updated_at`) VALUES
(26, 'Đánh giá chi tiết Lenovo ThinkPad X1 Nano: Một mẫu laptop mỏng nhẹ nhưng lại vô cùng mạnh mẽ', 'image/177OfXnLzmVfYI6jr7fBeNX1ibPZtt7fnm3L3Xa2.png', '<h2>Lenovo Thinkpad X1 Nano l&agrave; một mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\">laptop</a>&nbsp;c&oacute; ngoại h&igrave;nh ấn tượng thuộc&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/laptop-nhu-the-nao-thi-duoc-goi-la-ultrabook-519250\" target=\"_blank\">d&ograve;ng ultrabook</a>&nbsp;của&nbsp;<a href=\"https://www.thegioididong.com/laptop-lenovo\" target=\"_blank\">Lenovo</a>, mỏng nhẹ nhưng v&ocirc; c&ugrave;ng mạnh mẽ. Chiếc laptop n&agrave;y c&oacute; những đặc điểm tuyệt vời như Lenovo Thinkpad X1 Carbon nhưng c&oacute; khối lượng nhẹ hơn. Sau đ&acirc;y l&agrave; b&agrave;i&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/danh-gia-chi-tiet-lenovo-thinkpad-x1-nano-1354963\" target=\"_blank\">đ&aacute;nh gi&aacute; chi tiết Lenovo ThinkPad X1 Nano</a>&nbsp;dưới g&oacute;c nh&igrave;n của John Loeffler, reviewer của trang Techradar.pro.</h2>\r\n\r\n<p><em>B&agrave;i viết n&agrave;y được tham khảo từ chuy&ecirc;n trang c&ocirc;ng nghệ&nbsp;<a href=\"https://www.techradar.com/reviews/lenovo-thinkpad-x1-nano-review\" target=\"_blank\">Techradar.pro</a></em></p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/di-tim-nhung-chiec-laptop-gaming-dang-mua-nhat-trong-thang-6-nay-1358061\" target=\"_blank\">Đi t&igrave;m những chiếc laptop gaming đ&aacute;ng mua nhất trong th&aacute;ng 6 n&agrave;y</a></p>\r\n\r\n<h3><strong>Mốt số nhận định của t&aacute;c giả về Lenovo ThinkPad X1 Nano</strong></h3>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano24_800x450.png\" /></p>\r\n\r\n<p>Thinkpad X1 Nano, chiếc laptop xứng đ&aacute;ng d&agrave;nh cho doanh nh&acirc;n. Nguồn: Techradar.</p>\r\n\r\n<p>Lenovo Thinkpad X1 Nano l&agrave; một trong những mẫu laptop thuộc d&ograve;ng ultrabook (laptop chuy&ecirc;n mỏng, nhẹ, hiệu suất tốt v&agrave; thời lượng pin d&agrave;i) của Lenovo c&oacute; thiết kế với độ mỏng ấn tượng. C&oacute; lẽ đ&acirc;y l&agrave; mẫu laptop nhẹ nhất thuộc d&ograve;ng ultrabook m&agrave; reviewer của trang c&ocirc;ng nghệ techradar.pro đ&atilde; từng trải nghiệm. B&ecirc;n cạnh đ&oacute;, chiếc laptop n&agrave;y c&ograve;n được trang bị m&agrave;n h&igrave;nh 13 inch, độ ph&acirc;n giải 2K sắc n&eacute;t, chống ch&oacute;i.</p>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano25_800x450.png\" /></p>\r\n\r\n<p>M&agrave;n h&igrave;nh 2K sắc n&eacute;t được Lenovo trang bị tr&ecirc;n chiếc m&aacute;y n&agrave;y. Nguồn: Techradar.</p>\r\n\r\n<p>Ngo&agrave;i ra th&igrave; m&aacute;y c&ograve;n sử dụng tỉ lệ m&agrave;n h&igrave;nh l&agrave; 16:10 cho một g&oacute;c nh&igrave;n rộng gi&uacute;p l&agrave;m việc hiệu quả. Một điều đ&aacute;ng tiếc l&agrave; ThinkPad X1 Nano lại bị bỏ đi một số cổng kết nối (do h&atilde;ng phải đ&aacute;nh đổi với độ mỏng của m&aacute;y). Nếu bạn cần một chiếc laptop c&oacute; đầy đủ c&aacute;c cổng kết nối th&igrave; c&oacute; lẽ ThinkPad X1 Nano kh&ocirc;ng phải l&agrave; một sự lựa chọn ph&ugrave; hợp.</p>\r\n\r\n<h3><strong>Một số đ&aacute;nh gi&aacute; nhanh về chiếc laptop Thinkpad X1 Nano n&agrave;y</strong></h3>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano2_1280x720-800-resize.png\" /></p>\r\n\r\n<p>Thiết kế mỏng nhẹ của Thinkpad X1 Nano. Nguồn: Lenovo.</p>\r\n\r\n<p>Lenovo Thinkpad X1 Nano c&oacute; những đặc điểm tốt của Lenovo Thinkpad X1 Carbon nhưng chiếc laptop n&agrave;y nhẹ hơn v&agrave; c&oacute; phần n&agrave;o đ&oacute; tốt hơn nữa, theo đ&aacute;nh gi&aacute; của John Loeffler, t&aacute;c giả của b&agrave;i viết. Điểm đ&aacute;ng gi&aacute; nhất tr&ecirc;n chiếc X1 Nano n&agrave;y l&agrave; thiết bị cực kỳ nhẹ&nbsp;(khoảng 1kg). Nếu như sự di động l&agrave; một điểm bạn cần t&igrave;m tr&ecirc;n một chiếc laptop th&igrave; ThinkPad X1 Nano sẽ l&agrave; một sự lựa chọn kh&ocirc;ng thể n&agrave;o bỏ qua.</p>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano1_1280x720-800-resize.png\" /></p>\r\n\r\n<p>ThinkPad X1 Nano được trang bị một vi&ecirc;n pin c&oacute; k&iacute;ch thước kh&aacute; nhỏ v&agrave; được sạc qua cổng USB Type-C Thunderbolt 4. Nguồn: Lenovo.</p>\r\n\r\n<p>Ngo&agrave;i ra, ThinkPad X1 Nano c&ograve;n c&oacute; một vi&ecirc;n pin nhỏ hơn so với một số chiếc laptop thuộc d&ograve;ng ultrabook kh&aacute;c hiện nay. Mặc d&ugrave; vậy nhưng thiết bị vẫn được trang bị chuẩn c&ocirc;ng nghệ Intel Evo n&ecirc;n khiến chiếc laptop n&agrave;y lu&ocirc;n hoạt động hiệu quả với c&ocirc;ng suất tối đa. N&oacute;i chung, Lenovo ThinkPad X1 Nano c&oacute; thể đ&aacute;p ứng tốt nhu l&agrave;m việc trong cả ng&agrave;y d&agrave;i chỉ với một lần sạc.</p>\r\n\r\n<p>Trong tường hợp ch&uacute;ng ta sử dụng m&aacute;y với c&aacute;c t&aacute;c vụ nặng, đ&ograve;i hỏi thiết bị phải chạy với c&ocirc;ng suất cao hơn th&igrave; c&oacute; lẽ bạn sẽ phải cần đến d&acirc;y sạc. Thế nhưng, v&igrave; thinkPad X1 Nano được trang bị cổng USB Type-C Thunderbolt 4 n&ecirc;n điều đ&oacute; sẽ tiện lợi hơn rất nhiều, gi&uacute;p bạn tiết kiệm được thời gian sạc pin.</p>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano9_1280x720-800-resize.png\" /></p>\r\n\r\n<p>Mặc d&ugrave; mỏng nhẹ nhưng ThinkPad X1 Nano c&oacute; một cấu h&igrave;nh kh&ocirc;ng phải dạng vừa đ&acirc;u. Nguồn: Lenovo.</p>\r\n\r\n<p>Với trọng lượng si&ecirc;u mỏng nhẹ v&agrave; một cục pin nhỏ như vậy, nhiều người sẽ nghĩ rằng ThinkPad X1 Nano sẽ bị giảm một số thứ về mặt cấu h&igrave;nh. Tuy nhi&ecirc;n, chiếc laptop n&agrave;y vẫn sở hữu một sức mạnh đ&aacute;ng nể v&agrave; kh&ocirc;ng gặp bất k&igrave; vấn đề giật lag n&agrave;o khi thực hiện c&aacute;c t&aacute;c vụ nặng. Đ&acirc;y ch&iacute;nh l&agrave; điều khiến cho Lenovo Thinkpad X1 Nano trở th&agrave;nh chiếc laptop doanh nh&acirc;n tốt nhật hiện nay.</p>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano5_1280x720-800-resize.png\" /></p>\r\n\r\n<p>ThinkPad X1 Nano chỉ được trang bị 3 cổng kết nối. Nguồn: Lenovo.</p>\r\n\r\n<p>C&oacute; một số nhược điểm tr&ecirc;n chiếc ThinkPad X1 Nano n&agrave;y, chẳng hạn như số lượng cổng kết nối kh&ocirc;ng qu&aacute; đa dạng. Chiếc m&aacute;y n&agrave;y chỉ được trang bị 1 jack 3.5 mm v&agrave; 2 cổng USB Type-C. V&igrave; ThinkPad X1 Nano l&agrave; chiếc laptop hướng đến đối tượng doanh nh&acirc;n n&ecirc;n t&iacute;nh bảo mật rất được coi trọng. Ch&iacute;nh v&igrave; thế m&agrave; m&aacute;y c&ograve;n sở hữu một số t&iacute;nh băn bảo mật rất hữu dụng.</p>\r\n\r\n<h3><strong>Thiết kế của ThinkPad X1 Nano: Si&ecirc;u mỏng c&aacute;nh nhưng kh&ocirc;ng mỏng manh</strong></h3>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano6_1280x720-800-resize.png\" /></p>\r\n\r\n<p>Khung m&aacute;y được l&agrave;m bằng sợi carbon v&agrave; magie cực kỳ cứng c&aacute;p. Nguồn: Lenovo.</p>\r\n\r\n<p>Phải n&oacute;i rằng Lenovo Thinkpad X1 Nano được ho&agrave;n thiện với chất lượng rất cao, phần khung được l&agrave;m bằng sợi carbon v&agrave; magie gi&uacute;p m&aacute;y c&oacute; khối lượng nhẹ, bền bỉ. Chắc chắn l&agrave; ch&uacute;ng ta kh&ocirc;ng thể test thử độ bền của m&aacute;y bằng c&aacute;ch thả rơi em n&oacute; xuống đất được nhưng reviewer John Loeffler tin rằng ThinkPad X1 Nano chắc chắn c&oacute; thể sống s&oacute;t sau một c&uacute; rơi.</p>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano13_1280x720-800-resize.png\" /></p>\r\n\r\n<p>Khả năng gập 180 độ của ThinkPad X1 Nano mang lại nhiều g&oacute;c nh&igrave;n kh&aacute;c nhau. Nguồn: Lenovo.</p>\r\n\r\n<p>B&ecirc;n dưới phần th&acirc;n v&agrave;&nbsp;ph&iacute;a b&ecirc;n cạnh phải của Lenovo Thinkpad X1 đều c&oacute; những lỗ th&ocirc;ng hơi để kh&ocirc;ng kh&iacute; c&oacute; thể dễ d&agrave;ng tho&aacute;t ra trong qu&aacute; tr&igrave;nh tản nhiệt.&nbsp; Một trong những điểm đặc biệt của chiếc laptop n&agrave;y l&agrave; phần bản lề xoay gập 180 độ, gi&uacute;p ch&uacute;ng ta sẽ thay đổi được nhiều g&oacute;c nh&igrave;n kh&aacute;c nhau với m&agrave;n h&igrave;nh.</p>\r\n\r\n<p>Ngo&agrave;i ra ở phần bản lề của ThinkPad X1 Nano c&ograve;n được thiết kế để gi&uacute;p n&acirc;ng b&agrave;n ph&iacute;m l&ecirc;n một ch&uacute;t, tạo cảm gi&aacute;c thoải m&aacute;i hơn cho người d&ugrave;ng khi g&otilde; ph&iacute;m l&acirc;u v&agrave; gi&uacute;p thiết bị c&oacute; th&ecirc;m kh&ocirc;ng gian để tản nhiệt.</p>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano20_800x450.png\" /></p>\r\n\r\n<p>Trackpoint (chấm đỏ) vẫn xuất hiện ở giữa b&agrave;n ph&iacute;m của ThinkPad X1 Nano. Nguồn: Lenovo.</p>\r\n\r\n<p>Lenovo Thinkpad X1 Nano được trang bị tổng cộng 4 loa bao gồm 2 loa tần số cao ở ph&iacute;a tr&ecirc;n v&agrave; 2 loa trầm ở mặt đ&aacute;y của m&aacute;y. Hệ thống loa n&agrave;y c&ograve;n đạt được chứng chỉ Dolby Atmos v&agrave; hứa hẹn sẽ mang đến một trải nghiệm &acirc;m thanh tuyệt vời.</p>\r\n\r\n<p>Về cổng kết nối, ThinkPad X1 Nano chỉ c&oacute; 2 cổng USB Type-C Thunderbolt 4 v&agrave; 1 jack cắm tai nghe 3.5 mm. N&uacute;t nguồn của m&aacute;y th&igrave; được đặt ở b&ecirc;n cạnh phải của m&aacute;y, kế b&ecirc;n phần tản nhiệt. Theo trải nghiệm của t&aacute;c giả, phần tản nhiệt n&agrave;y kh&ocirc;ng phả ra qu&aacute; nhiều hơi n&oacute;ng trong qu&aacute; tr&igrave;nh anh ấy sử dụng (kể cả những t&aacute;c vụ nặng).</p>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano10_1280x720-800-resize.png\" /></p>\r\n\r\n<p>Khả năng tản nhiệt của ThinkPad X1 Nano l&agrave; rất ấn tượng. Nguồn: Lenovo.</p>\r\n\r\n<h3><strong>Trải nghiệm g&otilde; ph&iacute;m v&agrave; trackpad của ThinkPad X1 Nano</strong></h3>\r\n\r\n<p>Lenovo Thinkpad X1 Nano trang bị một b&agrave;n ph&iacute;m chiclet (c&aacute;c ph&iacute;m bấm được thiết kế l&otilde;m xuống để tạo cảm gi&aacute;c thoải m&aacute;i cho ng&oacute;n tay, khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m được sắp xếp hợp l&yacute;) đi k&egrave;m với Trackpoint (chấm đỏ) nằm ở giữa b&agrave;n ph&iacute;m, một điểm rất đặc trưng kh&ocirc;ng thể thiểu tr&ecirc;n c&aacute;c sản phẩm thuộc d&ograve;ng ThinkPad. B&agrave;n ph&iacute;m của ThinkPad X1 Nano cho một trải nghiệm bấm tốt, đầm tay v&agrave; c&oacute; h&agrave;nh tr&igrave;nh ph&iacute;m vừa phải, phản hồi nhanh.</p>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano19_800x450.png\" /></p>\r\n\r\n<p>Ph&iacute;m chức năng (Fn) được đặt ở g&oacute;c tr&aacute;i v&agrave; kh&aacute; xa trong qu&aacute; tr&igrave;nh g&otilde; ph&iacute;m. Nguồn: Techradar.</p>\r\n\r\n<p>Theo như cảm nhận của reviewer John Loeffler, điểm trừ duy nhất của b&agrave;n ph&iacute;m n&agrave;y l&agrave; vị trị đặt của ph&iacute;m chức năng (Fn). Hầu hết người d&ugrave;ng đ&atilde; quen với việc ph&iacute;m Ctrl nằm ở g&oacute;c dưới c&ugrave;ng b&ecirc;n tr&aacute;i nhưng Lenovo lại di chuyển ph&iacute;m Fn đến vị tr&iacute; đ&oacute;. Điều n&agrave;y đ&atilde; l&agrave;m cho t&aacute;c giả mất một thời gian để điều chỉnh lại th&oacute;i quen sử dụng ở giai đoạn đầu.</p>\r\n\r\n<p>Trackpad tr&ecirc;n ThinkPad X1 c&oacute; k&iacute;ch thước tương đối nhỏ v&igrave; phải nhường chỗ cho 3 ph&iacute;m n&uacute;t vật l&yacute; (chuột tr&aacute;i, ph&iacute;m giữa v&agrave; chuột phải). C&aacute; nh&acirc;n t&aacute;c giả nghĩ rằng 3 ph&iacute;m vật l&yacute; b&ecirc;n tr&ecirc;n trackpad l&agrave; kh&ocirc;ng cần thiết bởi v&igrave; người d&ugrave;ng vẫn c&oacute; thể nhấn ở g&oacute;c dưới c&ugrave;ng b&ecirc;n tr&aacute;i hoặc b&ecirc;n phải để thực hiện c&aacute;c t&aacute;c vụ tương tự như 3 ph&iacute;m vật l&yacute; ấy.</p>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano17_800x450.png\" /></p>\r\n\r\n<p>Loa của Thinkpad X1 Nano đạt chứng chỉ Dobly Atmos mang đến một trải nghiệm &acirc;m thanh kh&aacute; tốt. Nguồn: Techradar.</p>\r\n\r\n<h3><strong>Cấu h&igrave;nh của Thinkpad X1 Nano mạnh đến mức n&agrave;o?</strong></h3>\r\n\r\n<p>Trước khi đi v&agrave;o phần đ&aacute;nh gi&aacute; hiệu năng, m&igrave;nh sẽ điểm qua th&ocirc;ng số cấu h&igrave;nh của chiếc laptop n&agrave;y để ch&uacute;ng ta c&oacute; thể thấy được hiệu năng của Thinkpad X1 Nano mạnh mẽ đến nhường n&agrave;o.</p>\r\n\r\n<ul>\r\n	<li>CPU: Intel Core i7-1160G7</li>\r\n	<li>Card đồ hoạ: Intel Iris Xe</li>\r\n	<li>RAM: 16 GB DDR4</li>\r\n	<li>M&agrave;n h&igrave;nh: 13 inch, tỷ lệ 16:10, độ ph&acirc;n giải 2160x1350p IPS, độ s&aacute;ng 450 nits</li>\r\n	<li>Bộ nhớ trong: 512 GB PCIe SSD</li>\r\n	<li>Cổng kết nối: 2 x USB Type-C Thunderbolt 4, 1 jack cắm tai nghe 3.5 mm</li>\r\n	<li>Kết nối: Wi-Fi 6, Bluetooth 5.0</li>\r\n	<li>Camera: 720p hỗ trợ camera hồng ngoại</li>\r\n	<li>C&acirc;n nặng: 907 g</li>\r\n	<li>Pin: 48 WHr, c&oacute; hỗ trợ sạc nhanh</li>\r\n</ul>\r\n\r\n<p><img alt=\"danh-gia-chi-tiet-lenovo-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano7_1280x720-800-resize.png\" /></p>\r\n\r\n<p>Lenovo Thinkpad X1 Nano sỡ hữu cho m&igrave;nh một cấu h&igrave;nh mạnh mẽ c&oacute; thể gi&uacute;p bạn c&acirc;n được mọi t&aacute;c vụ. Nguồn: Lenovo.</p>\r\n\r\n<p>Phải n&oacute;i rằng Lenovo Thinkpad X1 Nano sở hữu m&ocirc;t hiệu năng kh&aacute; mạnh mẽ so với một chiếc laptop doanh nh&acirc;n th&ocirc;ng thường. Con chip Intel Core I7-1160G7 của ThinkPad X1 Nano đ&atilde; chứng minh cho ch&uacute;ng ta thấy được sức mạnh qua những b&agrave;i test chấm điểm hiệu năng</p>\r\n\r\n<p>Một số b&agrave;i test chấm điểm hiệu năng m&agrave; John Loeffler đo được bằng chiếc Lenovo Thinkpad X1 Nano m&agrave; anh ấy sử dụng để đ&aacute;nh gi&aacute;:&nbsp;</p>\r\n\r\n<p><img alt=\"danh-gia-chi-tiet-lenovo-thinkpad-x1-nano \" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/diemhieunangcuathinkpadx1thongquamotsobaitest1_1024x768-800-resize.png\" /></p>\r\n\r\n<p>Một số b&agrave;i test tr&ecirc;n Lenovo Thinkpad X1 Nano. Nguồn: Techradar.</p>\r\n\r\n<p>So với Razer Book 13 th&igrave; hiệu năng của ThinkPad X1 Nano chỉ k&eacute;m hơn một ch&uacute;t. Ở b&ecirc;n dưới l&agrave; h&igrave;nh so s&aacute;nh hiệu năng của bộ đ&ocirc;i laptop n&agrave;y th&ocirc;ng qua c&aacute;c điểm số được chấm bằng GeekBench 5 v&agrave; PCMark 10 Home.</p>\r\n\r\n<p><img alt=\"danh-gia-chi-tiet-lenovo-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chi-tiet-lenovo-thinkpad-x1-nano423424_800x605.png\" /></p>\r\n\r\n<p>So s&aacute;nh điểm số Geekbench giữa Lenovo Thinkpad X1 Nano với Razer Book 13. Nguồn: Techradar.</p>\r\n\r\n<p><img alt=\"danh-gia-chi-tiet-lenovo-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chi-tiet-lenovo-thinkpad-x1-nano23131_800x553.png\" /></p>\r\n\r\n<p>C&ograve;n đ&acirc;y l&agrave; so s&aacute;nh điểm PCMark 10 Home. Nguồn: Techradar.</p>\r\n\r\n<p>Trong hầu hết b&agrave;i test của 3DMark (chuy&ecirc;n d&ugrave;ng để test đồ họa) th&igrave; ThinkPad X1 Nano cho kết quả vượt trội hơn so với Dell XPS 13 (2020) nhưng lại kh&ocirc;ng thể thắng được Razer Book 13,). Tuy nhi&ecirc;n trong b&agrave;i test Time spy th&igrave; ThinkPad X1 Nano lại thua cả hai đối thủ của m&igrave;nh mặc d&ugrave; John Loeffler đ&atilde; đo lại rất nhiều lần (c&aacute;c bạn c&oacute; thể xem h&igrave;nh so s&aacute;nh b&ecirc;n dưới):</p>\r\n\r\n<h1><img alt=\"danh-gia-chi-tiet-lenovo-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chi-tiet-lenovo-thinkpad-x1-nano123131_800x550.png\" /></h1>\r\n\r\n<p>so s&aacute;nh điểm Time Spy giữa Lenovo Thinkpad X1 Nano với một số mẫu laptop kh&aacute;c. Nguồn: Techradar.</p>\r\n\r\n<h3><strong>Liệu ThinkPad X1 Nano c&oacute; mang lại thời lượng sử dụng pin l&acirc;u d&agrave;i?</strong></h3>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano12_1280x720-800-resize.png\" /></p>\r\n\r\n<p>Được trang bị một vi&ecirc;n pin tuy nhỏ nhưng Thinkpad X1 Nano ho&agrave;n to&agrave;n c&oacute; thể đ&aacute;p ứng đủ thời gian sử dụng của bạn. Nguồn: Lenovo.</p>\r\n\r\n<p>Về thời lượng pin sử dụng th&igrave; ThinkPad X1 Nano ho&agrave;n to&agrave;n c&oacute; thể ăn đứt cả Dell XPS 13 v&agrave; Razer Book 13 (do l&agrave; laptop doanh nh&acirc;n m&agrave;) mặc d&ugrave; vi&ecirc;n pin của thiết bị c&oacute; hơi nhỏ hơn một ch&uacute;t so với c&aacute;c đối thủ, theo nhận định của t&aacute;c giả.</p>\r\n\r\n<p><img alt=\"danh-gia-chi-tiet-lenovo-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chi-tiet-lenovo-thinkpad-x1-nano4232342_800x601.png\" /></p>\r\n\r\n<p>So s&aacute;nh dung lương pin của Lenovo Thinkpad X1 Nano với một số mẫu laptop kh&aacute;c. Nguồn: Techradar.</p>\r\n\r\n<p>Trong b&agrave;i test pin của PCMark 10 th&igrave; Dell XPS 13 đứng đầu với thời gian sử dụng l&agrave; 11 giờ 47 ph&uacute;t, tiếp theo l&agrave; Razer Book 13 11 giờ 42 ph&uacute;t v&agrave; cuối c&ugrave;ng l&agrave; X1 Nano với 11 giờ 28 ph&uacute;t. Thực chất th&igrave; kết quả ở tr&ecirc;n chỉ d&ugrave;ng để tham khảo m&agrave; th&ocirc;i v&igrave; trong qu&aacute; tr&igrave;nh sử dụng thực tế th&igrave; reviewer của Techradar.pro kh&ocirc;ng nhận thấy qu&aacute; nhiều sự kh&aacute;c biệt giữa 3 mẫu m&aacute;y.</p>\r\n\r\n<p>Trong b&agrave;i test xem video HD th&igrave; ThinkPad X1 Nano lại cho một thời lượng sử dụng pin đ&aacute;ng kinh ngạc với 11 giờ, đ&aacute;nh bại ho&agrave;n to&agrave;n Razer Book 13 chỉ c&oacute; 9 giờ 20 ph&uacute;t v&agrave; chỉ thua thời gian sử dụng pin của Dell XPS 13 l&agrave; 1 ph&uacute;t. N&oacute;i chung th&igrave; đại diện đến từ Lenovo cũng cho ch&uacute;ng ta một thời lượng sử dụng pin rất ấn tượng.</p>\r\n\r\n<h3><strong>Một số t&iacute;nh năng v&agrave; phần mềm th&uacute; vị của Thinkpad X1 Nano</strong></h3>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano_800x450.png\" /></p>\r\n\r\n<p>Một số t&iacute;nh năng bảo mật th&ocirc;ng minh được Lenovo trang bị tr&ecirc;n sản phẩm của m&igrave;nh. Nguồn: Lenovo.</p>\r\n\r\n<p>May mắn l&agrave; hầu như kh&ocirc;ng c&oacute; bất kỳ phần mềm bloatware (phần mềm b&ecirc;n thứ 3) n&agrave;o được c&agrave;i sẵn tr&ecirc;n chiếc Lenovo Thinkpad X1 Nano n&agrave;y. Điều n&agrave;y cho thấy Lenovo đang hướng đến những trải nghiệm tốt nhất d&agrave;nh cho người d&ugrave;ng d&ograve;ng laptop doanh nh&acirc;n của h&atilde;ng v&agrave; thậm ch&iacute; l&agrave; cả cộng đồng doanh nghiệp.</p>\r\n\r\n<p>V&igrave; đ&acirc;y l&agrave;m một chiếc laptop hướng đến đối tượng người d&ugrave;ng l&agrave; doanh nh&acirc;n n&ecirc;n h&atilde;ng đ&atilde; trang bị cho Thinkpad X1 Nano rất nhiều t&iacute;nh năng bảo mật cao cấp. V&iacute; dụ như cảm biến v&acirc;n tay, BIOS c&oacute; thể tự kh&ocirc;i phục v&agrave; camera IR (camera hồng ngoại) gi&uacute;p người d&ugrave;ng c&oacute; thể mở kho&aacute; bằng khu&ocirc;n mặt, tự động kho&aacute; chiếc laptop khi người d&ugrave;ng quay đi hoặc rời mắt khỏi m&agrave;n h&igrave;nh.</p>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano26_800x450.png\" /></p>\r\n\r\n<p>Ngo&agrave;i ra, phần webcam của m&aacute;y c&ograve;n c&oacute; th&ecirc;m phần gạt che lại camera khi người d&ugrave;ng kh&ocirc;ng d&ugrave;ng đến. Nguồn: Lenovo.</p>\r\n\r\n<h3><strong>Tổng kết</strong></h3>\r\n\r\n<p>ThinkPad X1 Nano thật sự xứng đ&aacute;ng l&agrave; người kế nhiệm ho&agrave;n hảo cho d&ograve;ng X1 Carbon huyền thoại v&agrave; cũng l&agrave; chiếc laptop doanh nh&acirc;n đ&aacute;ng mua nhất hiện nay khi c&oacute; qu&aacute; nhiều ưu điểm như một cấu h&igrave;nh mạnh mẽ, t&iacute;nh di dộng cao... v&agrave; được Lenovo trang bị rất nhiều t&iacute;nh năng th&ocirc;ng minh kh&aacute;c.&nbsp;</p>\r\n\r\n<p><img alt=\"danh-gia-chiec-thinkpad-x1-nano\" src=\"https://cdn.tgdd.vn/Files/2021/05/26/1354963/danh-gia-chiec-thinkpad-x1-nano11_800x450.png\" /></p>\r\n\r\n<p>C&acirc;n nhắc giữa những nhu cầu sử dụng laptop của bạn khi chọn Thinkpad X1 Nano l&agrave;m người bạn đồng h&agrave;nh của m&igrave;nh nh&eacute;. Nguồn: Lenovo.</p>\r\n\r\n<p>Vậy bạn nghĩ sao về Lenovo Thinkpad X1 Nano? Chiếc m&aacute;y n&agrave;y c&oacute; ph&ugrave; hợp với nhu cầu sử dụng của bạn hay kh&ocirc;ng? H&atilde;y b&igrave;nh luận ở b&ecirc;n dưới cho m&igrave;nh biết với nh&eacute;!</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/lenovo-tab-m7-da-re-nay-con-duoc-giam-gia-tung-bung-1354674\" target=\"_blank\">Lenovo Tab M7 đ&atilde; rẻ n&agrave;y c&ograve;n được giảm gi&aacute; tưng bừng, nhanh tay sắm về</a></p>\r\n\r\n<p>Bi&ecirc;n tập bởi Quốc Huy</p>', '2021-06-25 09:31:33', '2021-06-26 10:17:03'),
(27, 'Deal HOT tháng 6: Loạt laptop Lenovo giảm sốc, toàn máy xịn mà giá tiết kiệm đến tiền triệu, tranh thủ nào các bạn', 'image/TC897UowZSManBniog3nmjaoZHK20heKvUyyJ4Yp.jpg', '<h1>Deal HOT th&aacute;ng 6: Loạt laptop Lenovo giảm sốc, to&agrave;n m&aacute;y xịn m&agrave; gi&aacute; tiết kiệm đến tiền triệu, tranh thủ n&agrave;o c&aacute;c bạn</h1>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tin-tuc/profile/0\">Pham Thang</a>&nbsp;2 ng&agrave;y trước</p>\r\n\r\n<p><img alt=\"Deal HOT tháng 6: Laptop Lenovo giảm sốc, tranh thủ nào\" src=\"https://cdn.tgdd.vn/Files/2021/06/22/1362267/rethumb_1280x720-800-resize.jpg\" /></p>\r\n\r\n<h2>Những ng&agrave;y cuối th&aacute;ng 6 n&agrave;y, ai đang c&oacute; dự định sắm&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\">laptop</a>&nbsp;mới th&igrave; kh&ocirc;ng n&ecirc;n bỏ qua loạt&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/deal-hot-thang-6-danh-cho-laptop-lenovo-1362267\" target=\"_blank\">laptop&nbsp;Lenovo&nbsp;giảm sốc</a>. Rất nhiều deal cực k&igrave; HOT vẫn đang diễn ra, to&agrave;n m&aacute;y xịn gi&aacute; hời, chưa kể c&ograve;n c&oacute; cả những phần qu&agrave; gi&aacute; trị tặng k&egrave;m khi mua. Cho n&ecirc;n h&atilde;y tranh thủ ngay h&ocirc;m nay nha c&aacute;c bạn!</h2>\r\n\r\n<p><strong>Thời gian diễn ra</strong>: Đến ng&agrave;y 30/6/2021.</p>\r\n\r\n<p><strong>Lưu &yacute;:</strong></p>\r\n\r\n<ul>\r\n	<li>Khuyến m&atilde;i chỉ &aacute;p dụng đặt mua online.</li>\r\n	<li>Khuyến m&atilde;i &aacute;p dụng t&ugrave;y sản phẩm.</li>\r\n</ul>\r\n\r\n<h3><strong>Lenovo Gaming giảm đến 4 triệu</strong>&nbsp;</h3>\r\n\r\n<p><strong>Lenovo Legion 5 15IMH05 i7 10750H (82AU0051VN)</strong></p>\r\n\r\n<p>Mở đầu d&agrave;nh s&aacute;ch n&agrave;y sẽ l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/laptop/lenovo-legion-5-15imh05-i7-82au0051vn\" target=\"_blank\">Lenovo Legion 5 15IMH05 i7 10750H (82AU0051VN)</a>, b&ecirc;n cạnh thiết kế hiện đại th&igrave; chiếc m&aacute;